import React from 'react';
import styled from 'styled-components';
import { IoSettingsOutline, IoCalendarNumberOutline } from 'react-icons/io5';
import { useRecoilState } from 'recoil';
import { settingModalOpenAtom, showDropDownAtom } from 'atoms';

const Header = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  position: relative;
  height: 84px;
  padding: 7px 10px;
  margin-bottom: 14px;
  border-width: 0.1px 0.1px 0px 0.1px;
  border-style: solid;
  border-color: #000000;
  box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
  border-radius: 10px;
  cursor: pointer;
  &:hover {
    background: #e7e7e7;
  }
  &:active {
    background: #d8d8d8;
  }
`;
const HeaderLeft = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 71px;
  height: 71px;
  border-radius: 71px;
  border: 1px solid black;
  margin-right: 5px;
`;
const HeaderImg = styled.div`
  width: 65px;
  height: 65px;
  border-radius: 65px;
  background: url('data:image/jpeg;base64,');
  background-size: contain;
  background-repeat: no-repeat;
`;
const HeaderRight = styled.div`
  display: flex;
  flex-direction: column;
`;
const HeaderTeamname = styled.div`
  margin-bottom: 6px;
  font-family: 'IBM Plex Sans', sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: 18px;
  line-height: 23px;
`;
const HeaderTeamSet = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 8px;
  margin-bottom: 7px;
  a {
    color: #878787;
    font-family: 'IBM Plex Sans', sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: 9px;
    line-height: 12px;
    text-decoration: underline;
    cursor: pointer;
    &:hover {
      color: #494949;
    }
    svg {
      scale: 1.2;
      margin-right: 2px;
      transform: translateY(1px);
    }
  }
`;
const HeaderTeamPub = styled.div`
  margin-left: 8px;
  color: #878787;
  font-family: 'IBM Plex Sans', sans-serif;
  font-style: italic;
  font-weight: 600;
  font-size: 8px;
  line-height: 10px;
`;

interface Props {
  teamname: string;
}

function Teams({ teamname }: Props) {
  const [settingModalOpen, setSettingModalOpen] =
    useRecoilState(settingModalOpenAtom);
  const [showDropDown, setShowDropDown] = useRecoilState(showDropDownAtom);

  const handleShowTeamSetting = () => {
    setSettingModalOpen(true);
  };

  const handleShowDropDown = (e: any) => {
    e.target.tagName !== 'A' && setShowDropDown((prev) => !prev);
  };

  return (
    <Header onClick={handleShowDropDown}>
      <HeaderLeft>
        <HeaderImg />
      </HeaderLeft>
      <HeaderRight>
        <HeaderTeamname>{teamname}</HeaderTeamname>
        <HeaderTeamSet>
          <a onClick={handleShowTeamSetting}>
            <IoSettingsOutline />
            Team Settings
          </a>
          <a>
            <IoCalendarNumberOutline />
            Team Plan
          </a>
        </HeaderTeamSet>
        <HeaderTeamPub>2022/1/17 published</HeaderTeamPub>
      </HeaderRight>
    </Header>
  );
}

export default React.memo(Teams);
